mapper.select:
SELECT * FROM app_category

	
		parentId = #{pid}
		parentId is null

select a.id ,a.*, a.softwareName , a.APKName , a.softwareSize, (select valueName from data_dictionary where typeCode='APP_FLATFORM' and valueId=a.flatformId) as valueName, (select categoryName from app_category where id=a.categoryLevel1) as categoryName1, (select categoryName from app_category where id=a.categoryLevel2) as categoryName2, (select categoryName from app_category where id=a.categoryLevel3) as categoryName3, (select valueName from data_dictionary where typeCode='APP_STATUS' and valueId=a.`status`) as valueName1, a.downloads , (select versionNo from app_version where appId=a.id ORDER BY modifyDate DESC LIMIT 0,1) as versionNo from app_info a
and a.softwareName like CONCAT('%',#{softwareName},'%')
and a.status=#{status} and a.flatformId=#{flatformId} and a.categoryLevel1=#{categoryLevel1} and a.categoryLevel2=#{categoryLevel2} and a.categoryLevel3=#{categoryLevel3}
LIMIT #{index},#{currentPageNo}

select count(*) from app_info a

	and a.softwareName like CONCAT('%',#{softwareName},'%')
	
	and a.status=#{status}
	and a.flatform Id=#{flatformId}
	and a.categoryLevel1=#{categoryLevel1}
	and a.categoryLevel2=#{categoryLevel2}
	and a.categoryLevel3=#{categoryLevel3}
